import { useState } from "react";

function AddLoad({ addLoad }) {
  const [formData, setFormData] = useState({
    pickupAddress: "",
    dropoffAddress: "",
    pickupDate: "",
    dropoffDate: "",
    pickupTime: "",
    dropoffTime: "",
    weight: "",
    rate: ""
  })
  const [submitted, setSubmitted] = useState("")
  const [checkArt, setCheckArt] = useState("")

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    fetch("http://localhost:9292/Loads", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(formData)
    })
    .then(resp => resp.json())
    .then(newLoad => {
      console.log(newLoad)
      setFormData({
        pickupAddress: "",
        dropoffAddress: "",
        pickupDate: "",
        dropoffDate: "",
        pickupTime: "",
        dropoffTime: "",
        weight: "",
        rate: ""
      })
      addLoad(newLoad)
      setSubmitted("Your art was submitted!")
      setCheckArt("To see your art in the collection, navigate to the Arts tab.")
    })  
  }

  return (
    <div className="form-style-8">
      <h2>Add a new load</h2>
      <h3>Fill out the form below to add a new load</h3>
      <form onSubmit={handleSubmit}>
        <input type="text" name="pickup address" placeholder="Pickup Address" value={formData.pickupAddress} onChange={handleChange}/>
        <input type="text" name="dropoff address" placeholder="Dropoff Address" value={formData.dropoffAddress} onChange={handleChange}/>
        <input type="text" name="pickup date" placeholder="Pickup Date(mm/dd/yyyy)" value={formData.pickupDate} onChange={handleChange}/>
        <input type="text" name="dropoff date" placeholder="Dropoff Date(mm/dd/yyyy)" value={formData.dropoffDate} onChange={handleChange}/>
        <input type="text" name="pickup time" placeholder="Pickup Time" value={formData.pickupTime} onChange={handleChange}/>
        <input type="text" name="dropoff time" placeholder="Dropoff Time" value={formData.dropoffTime} onChange={handleChange}/>
        <input type="text" name="weight" placeholder="Weight" value={formData.weight} onChange={handleChange}/>
        <input type="text" name="rate" placeholder="Rate" value={formData.rate} onChange={handleChange}/>

        {/* use the dropdown below if necessary */}
        {/* <input type="text" name="accidents" placeholder="Accidents" value={formData.accidents} onChange={handleChange}/>
        <select value={formData.dl} name="dl" onChange={handleChange}>
          <option value="none">Driver's License Class</option>
          <option value="A">Class A</option>
          <option value="B">Class B</option>
          <option value="C">Class C</option>
        </select> */}
        <br></br>
        <button id="button" type="submit">Submit</button>
        <br></br>
        <p className="submitted">{submitted}</p>
        <p className="submitted">{checkArt}</p>
      </form>
    </div>
  )
}

export default AddLoad;
