import { useRouteMatch, useParams } from 'react-router-dom'
import Load from './Load';

function LoadsPage({ loads, deleteLoad }) {
  const match = useRouteMatch()
  const params = useParams()

  function handleDrugTest() {
    setDrugTestPassed(!drugTestPassed)
    fetch(`http://localhost:9292/${match.url}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      passed_drug_test: !drugTestPassed
    })
  })
    .then((r) => r.json())
    .then((updated) => {
      console.log(updated)
      onUpdateDriver(updated)
    });
  }

  const loadsList = loads.map(load => {
    return (
      <li key={load.id}>
        <Load load={load} deleteLoad={deleteLoad}/>
      </li>
    )
  })

  return (
    <div className="display">
      <h1>Loads:</h1>
      <ul>
        {loadsList}
      </ul>
    </div>
  )
}

export default LoadsPage;
