import { useParams } from 'react-router-dom';
import { useState } from 'react';

function DisplayDriver({ drivers }) {
  const [drugTest, setDrugTest] = useState(false)
  const [drivingRecord, setDrivingRecord] = useState(false)
  const params = useParams()
  // console.log(params)
  const matched = drivers.filter(driver => {
    return Number(params.id) === driver.id
  })

  function handleChangeDrugTest(e) {
    setDrugTest(e.target.checked)
  }

  function handleDrivingRecord(e) {
    setDrivingRecord(e.target.checked)

    fetch(`http://localhost:9292/Drivers/${matched[0].id}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      clean_driving_record: !matched[0].drivingRecord,
    }),
  })
    .then((r) => r.json())
    .then((updatedItem) => console.log(updatedItem));
  }

  let matchedObj = matched[0]

  if (!matchedObj) {
    return (
      <>
        <br></br>
        <br></br>
        <br></br>
        <h1>Driver was removed successfully.</h1>
        <h3>To go back, navigate to the Drivers tab.</h3>
      </>
    )
  } else {
    return (
      <div className="card">
        <img src={matched[0].img_url} alt="image" />
        <h2>{matched[0].name}</h2>
        <p>Age: {matched[0].age}</p>
        <p>Years of Experience: {matched[0].years_of_experience}</p>
        <p>Accidents: {matched[0].accidents}</p>
        <p>Driver's License Class: {matched[0].dl_class}</p>
        <ul>
          <li>
            <label>
              Did this driver pass the drug test?
              <br></br>
              <input type="checkbox" onChange={handleChangeDrugTest} checked={drugTest}></input>
            </label>
          </li>
          <li>
            <label>
              Does this driver has a clean driving record?
              <br></br>
              <input type="checkbox" onChange={handleDrivingRecord} checked={drivingRecord}> 
              </input>
            </label>
          </li>
        </ul>  
      </div>
    )
  }
}

export default DisplayDriver;
