import { BrowserRouter, NavLink, Route, Switch } from 'react-router-dom';
import { useState, useEffect } from 'react';
import './index.css'
import './App.css';
import Home from './components/Home';
import Loads from './components/Loads';
import DriversPage from './components/DriversPage';
import AddDriver from './components/AddDriver';
import AddLoad from './components/AddLoad';
import AddTruck from './components/AddTruck';

function App() {
  // const [arts, setArts] = useState([])
  const [arts, setArts] = useState([])


  useEffect(() => {
    fetch(`http://localhost:9292/Drivers`)
    .then(r => r.json())
    .then(driversData => setArts(driversData))
  }, [])

  function addArt(newArt) {
    console.log(newArt)
    const newArtsArray=[...arts, newArt]
    setArts(newArtsArray)
  }

  function toggle() {
    let x = document.getElementById("myTopnav");
    if (x.className === "topnav") {
      x.className += " responsive";
    } else {
      x.className = "topnav";
    }
  }

  return (
    <div className="App">
      <BrowserRouter>   
      <div className="topnav" id="myTopnav">
      <NavLink activeClassName="active" exact to="/">Home</NavLink>
      <NavLink activeClassName="active" exact to="/Loads">Loads</NavLink>
      <NavLink activeClassName="active" exact to="/Drivers">Drivers</NavLink>
      <div className="dropdown">
        <button className="dropbtn">New
          <i className="fa fa-caret-down"></i>
        </button>
        <div className="dropdown-content">
          <NavLink activeClassName="active" exact to="/AddLoad">Load</NavLink>
          <NavLink activeClassName="active" exact to="/AddTruck">Truck</NavLink>
          <NavLink activeClassName="active" exact to="/AddDriver">Driver</NavLink>
        </div>
      </div>
      <a className="icon" onClick={toggle}>&#9776;</a>
    </div>
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/Loads">
            <Loads />
          </Route>
          <Route path="/Drivers">
            <DriversPage arts={arts} />
          </Route>
          <Route path="/AddDriver">
            <AddDriver addArt={addArt}/>
          </Route>
          <Route path="/AddLoad">
            <AddLoad />
          </Route>
          <Route path="/AddTruck">
            <AddTruck />
          </Route>
        </Switch>
      </BrowserRouter>
    </div>
  )
}

export default App;
