function DriverCard({ name, image, id, onRemoveDriver }) { 

  function handleDelete() {
    fetch(`http://localhost:9292/${id}`, {
    method: "DELETE",
  })
    .then((r) => r.json())
    .then((driver) => {
      onRemoveDriver(driver)
    });
  }

  return (
    <div>
      <div className="card">
      <img src={image} alt={"art name"} />
      <h3 className="art-name">"{name}"</h3>
      <button className="card-btn">Edit</button>
      <br></br>
      <button className="card-btn delete" onClick={handleDelete}>Remove</button>
      </div>
    </div>
  )
}

export default DriverCard;
