import { BrowserRouter, NavLink, Route, Switch } from 'react-router-dom';
import { useState, useEffect } from 'react';
import './index.css'
import './App.css';
import Home from './components/Home';
import About from './components/About';
import DriversPage from './components/DriversPage';
import AddDriver from './components/AddDriver';

function App() {
  const [arts, setArts] = useState([])
  const [navbarOpen, setNavbarOpen] = useState(false)

  useEffect(() => {
    fetch(`http://localhost:3000/arts`)
    .then(r => r.json())
    .then(artsdata => setArts(artsdata))
  }, [])

  function addArt(newArt) {
    console.log(newArt)
    const newArtsArray=[...arts, newArt]
    setArts(newArtsArray)
  }

  const closeMenu = () => {
    setNavbarOpen(false)
  }

  return (
    <div className="App">     
      <BrowserRouter>



        <NavLink
        exact to="/"
        activeClassName="active-link"
        onClick={() => closeMenu()}
        >
        Home
        </NavLink>

        <NavLink
        exact to="/About"
        activeClassName="active-link"
        onClick={() => closeMenu()}
        >
        About
        </NavLink>

        <NavLink
        exact to="/Drivers"
        activeClassName="active-link"
        onClick={() => closeMenu()}
        >
        Drivers
        </NavLink>

        <NavLink
        exact to="/AddDriver"
        activeClassName="active-link"
        onClick={() => closeMenu()}
        >
        Add Driver
        </NavLink>




        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/About">
            <About />
          </Route>
          <Route path="/Drivers">
            <DriversPage arts={arts} />
          </Route>
          <Route path="/AddDriver">
            <AddDriver addArt={addArt}/>
          </Route>
        </Switch>
      </BrowserRouter>
    </div>
  )
}

export default App;
