import { useState } from "react";

function AddDriver({ addArt }) {
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    experience: "",
    accidents: "",
    dl: ""
  })
  const [submitted, setSubmitted] = useState("")
  const [checkArt, setCheckArt] = useState("")

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    fetch("http://localhost:3000/arts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(formData)
    })
    .then(resp => resp.json())
    .then(newArt => {
      console.log(newArt)
      setFormData({
        name: "",
        age: 0,
        experience: 0,
        accidents: 0,
        dl: ""
      })
      addArt(newArt)
      setSubmitted("Your art was submitted!")
      setCheckArt("To see your art in the collection, navigate to the Arts tab.")
    })  
  }

  return (
    <div className="form-style-8">
      <h2>Add a new driver</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Driver's Name" value={formData.name} onChange={handleChange}/>
        <input type="number" name="age" placeholder="Driver's Age" value={formData.age} onChange={handleChange}/>
        <input type="number" name="experience" placeholder="Years of Experience" value={formData.experience} onChange={handleChange}/>
        <input type="number" name="accidents" placeholder="Accidents" value={formData.accidents} onChange={handleChange}/>
        {/* <input type="number" min={0} max={new Date().getFullYear()} name="year" placeholder="Year" value={formData.year} onChange={handleChange}/> */}
        <select value={formData.dl} name="dl" onChange={handleChange}>
          <option value="none">Driver's License Class</option>
          <option value="A">Class A</option>
          <option value="B">Class B</option>
          <option value="C">Class C</option>
        </select>
        <br></br>
        <button id="button" type="submit">Add Driver</button>
        <br></br>
        <p className="submitted">{submitted}</p>
        <p className="submitted">{checkArt}</p>
      </form>
    </div>
  )
}

export default AddDriver;
