import { Link } from 'react-router-dom';

function Load({ load, deleteLoad }) {

  function handleDelete() {
    fetch(`http://localhost:9292/Loads/${load.id}`, {
      method: "DELETE"
    })
    .then(r => r.json())
    .then(data => {
      deleteLoad(load.id)
    })
  }

  return (
    <div>
      <h3>Load Number #{load.id}</h3>
      <button className="selected" onClick={handleDrugTest}>{drugTestPassed ? "☑️" : "☐"}</button>
        <p> Check the box below if the driver has a clean driving record.</p>
        <button className={drivingRecord ? "selected" : ""} onClick={handleDrivingRecord}>{drivingRecord ? "☑️" : "☐"}</button>
      <Link to={`Drivers/${load.driver_id}`}>
        <p>Click to see the driver for this load</p>
      </Link>
      <p> ‣ Pickup Address: {load.pickup_address}</p>
      <p> ‣ Dropoff Address: {load.dropoff_address}</p>
      <p> ‣ Pickup Date: {load.pickup_date}</p>
      <p> ‣ Dropoff Date: {load.dropoff_date}</p>
      <p> ‣ Pickup Time: {load.pickup_time}</p>
      <p> ‣ Dropoff Time: {load.dropoff_time}</p>
      <p> ‣ Weight: {load.weight}</p>
      <p> ‣ Rate: {load.rate}</p>
      <button onClick={handleDelete}>Remove Load</button>
    </div>
  )
}

export default Load;
