import { Link, useRouteMatch } from 'react-router-dom';
import { useState } from 'react';

function Load({ load, deleteLoad, handleOnTheWay}) {
  const [onTheWay, setOnTheWay] = useState(false)
  let match = useRouteMatch()

  function handleDelete() {
    fetch(`http://localhost:9292/Loads/${load.id}`, {
      method: "DELETE"
    })
    .then(r => r.json())
    .then(() => {
      deleteLoad(load.id)
    })
  }

  function handleOnTheWay() {
    setOnTheWay(!onTheWay)
    fetch(`http://localhost:9292/Loads/${load.id}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      on_the_way: onTheWay
    })
  })
    .then((r) => r.json())
    .then((updated) => {
      console.log(updated)
      handleOnTheWay(updated)
    });
  }

  return (
    <div>
      <h3>Load Number #{load.id}</h3>
      <p>Is the load on the way?</p>
      <button className="selected" onClick={handleOnTheWay}>{onTheWay ? "☑️" : "☐"}</button>
        <p> Check the box below if the driver has a clean driving record.</p>
      <Link to={`Drivers/${load.driver_id}`}>
        <p>Click to see the driver for this load</p>
      </Link>
      <p> ‣ Pickup Address: {load.pickup_address}</p>
      <p> ‣ Dropoff Address: {load.dropoff_address}</p>
      <p> ‣ Pickup Date: {load.pickup_date}</p>
      <p> ‣ Dropoff Date: {load.dropoff_date}</p>
      <p> ‣ Pickup Time: {load.pickup_time}</p>
      <p> ‣ Dropoff Time: {load.dropoff_time}</p>
      <p> ‣ Weight: {load.weight}</p>
      <p> ‣ Rate: {load.rate}</p>
      <button onClick={handleDelete}>Remove Load</button>
    </div>
  )
}

export default Load;
