import { Link, useRouteMatch, Route, Switch } from 'react-router-dom';
import DriverCard from "./DriverCard";
import DisplayArt from "./DisplayArt";

function DriversList({ drivers }) {
  const match = useRouteMatch()

  const driversArray = drivers.map(driver => {
    return (
      <li key={driver.id}>
        <Link to={`Drivers/${driver.name}`}>
          <DriverCard name={driver.name} artist={driver.age} image= {driver.years_of_experience} material={driver.accidents} style={driver.style} year={driver.year}/>
        </Link>
      </li>
    )
  })

  return (
    <>
      <h2 className="header-list">Click an art for more details!</h2>
      <ul className="cards">
        {driversArray}
      </ul>
    </>
  )
}

export default DriversList;
