import { useParams } from 'react-router-dom';
import { useState } from 'react';

function DisplayDriver({ drivers }) {
  const [drugTestPassed, setDrugTestPassed] = useState(false)
  const [drivingRecord, setDrivingRecord] = useState(false)

  const params = useParams()
  // console.log(params)
  const matched = drivers.filter(driver => {
    return Number(params.id) === driver.id
  })

  function handleChangeDrugTest(e) {
    setDrugTest(e.target.checked)

    fetch(`http://localhost:9292/Drivers/${matched[0].id}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      passed_drug_test: !matched[0].drugTest,
    })
  })
    .then((r) => r.json())
    .then((updated) => console.log(updated));
  }

  function handleDrivingRecord(e) {
    setDrivingRecord(e.target.checked)

    fetch(`http://localhost:9292/Drivers/${matched[0].id}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      clean_driving_record: !matched[0].drivingRecord,
    })
  })
    .then((r) => r.json())
    .then((updated) => console.log(updated));
  }


  let matchedObj = matched[0]

  if (!matchedObj) {
    return (
      <>
        <br></br>
        <br></br>
        <br></br>
        <h1>Driver was removed successfully.</h1>
        <h3>To go back, navigate to the Drivers tab.</h3>
      </>
    )
  } else {
    return (
      <div className="card">
        <img src={matched[0].img_url} alt="image" />
        <h2>{matched[0].name}</h2>
