import { Link, useRouteMatch, Route, Switch } from 'react-router-dom';
import DriverCard from "./DriverCard";
import DisplayArt from "./DisplayArt";

function DriversList({ drivers }) {
  const match = useRouteMatch()

  function handleRemoveDriver(removedDriver) {
    console.log("in driver list:", removedDriver);
  }

  const driversArray = drivers.map(driver => {
    return (
      <li key={driver.id}>
        <Link to={`Drivers/${driver.name}`}>
          <DriverCard h name={driver.name} id= {driver.id} age={driver.age} experience= {driver.years_of_experience} class={driver.dl_class} image={driver.img_url}/>
        </Link>
      </li>
    )
  })

  return (
    <>
      <h2 className="header-list">Click on a driver to see more!</h2>
      <ul className="cards">
        {driversArray}
      </ul>
    </>
  )
}

export default DriversList;
