import { useParams } from 'react-router-dom';
import { useState } from 'react';

function DisplayDriver({ drivers }) {
  const [drugTest, setDrugTest] = useState(false)
  const [drivingRecord, setDrivingRecord] = useState(false)
  const params = useParams()
  // console.log(params)
  const matched = drivers.filter(driver => {
    return Number(params.id) === driver.id
  })

  function handleChangeDrugTest(e) {
    setDrugTest(e.target.value)

    fetch(`http://localhost:9292/Drivers/${matched[0].id}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      passed_drug_test: !matched[0].drugTest,
    })
  })
    .then((r) => r.json())
    .then((updated) => console.log(updated));
  }

  function handleDrivingRecord(e) {
    setDrivingRecord(e.target.value)

    fetch(`http://localhost:9292/Drivers/${matched[0].id}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      clean_driving_record: !matched[0].drivingRecord,
    })
  })
    .then((r) => r.json())
    .then((updated) => console.log(updated));
  }


  let matchedObj = matched[0]

  if (!matchedObj) {
    return (
      <>
        <br></br>
        <br></br>
        <br></br>
        <h1>Driver was removed successfully.</h1>
        <h3>To go back, navigate to the Drivers tab.</h3>
      </>
    )
  } else {
    return (
      <div className="card">
        <img src={matched[0].img_url} alt="image" />
        <h2>{matched[0].name}</h2>
        <p>Age: {matched[0].age}</p>
        <p>Years of Experience: {matched[0].years_of_experience}</p>
        <p>Accidents: {matched[0].accidents}</p>
        <p>Driver's License Class: {matched[0].dl_class}</p>
        <div>
          {/* <p>Did this driver pass the drug test?</p>
          <button onClick={handleChangeDrugTest} value={drugTest} className={drugTest ? "selected" : ""}>YES</button>
          <button onClick={handleChangeDrugTest} value={drugTest} className={drugTest ? "selected" : ""}>NO</button>




          <p>Does this driver has a clean driving record?</p>
          <button onClick={handleDrivingRecord} value={drivingRecord} className={drivingRecord ? "selected" : ""}>YES</button>
          <button onClick={handleDrivingRecord} value={drivingRecord} className={drivingRecord ? "selected" : ""}>NO</button> */}


        <input
        checked={isOn}
        onChange={handleToggle}
        className="react-switch-checkbox"
        id={`react-switch-new`}
        type="checkbox"
      />
      <label
        className="react-switch-label"
        htmlFor={`react-switch-new`}
      >
        <span className={`react-switch-button`} />
      </label>








        </div>
      </div>
    )
  }
}

export default DisplayDriver;
