import { BrowserRouter, NavLink, Route, Switch } from 'react-router-dom';
import { useState, useEffect } from 'react';
import './index.css'
import './App.css';
import Home from './components/Home';
import About from './components/About';
import DriversPage from './components/DriversPage';
import AddDriver from './components/AddDriver';

function App() {
  const [arts, setArts] = useState([])
  const [isClicked, setIsClicked] = useState(false)

  useEffect(() => {
    fetch(`http://localhost:3000/arts`)
    .then(r => r.json())
    .then(artsdata => setArts(artsdata))
  }, [])

  function addArt(newArt) {
    console.log(newArt)
    const newArtsArray=[...arts, newArt]
    setArts(newArtsArray)
  }

  function toggle() {

  }

  return (
    // <div className="App">
    <div>
      <BrowserRouter>




  	<input className="menu-icon" type="checkbox" id="menu-icon" name="menu-icon"/>
  	<label htmlFor="menu-icon"></label>
  	<nav className="nav"> 		
  		<ul className="pt-5">
  			<li onClick={toggle}><NavLink activeClassName="none" exact to="/">Home</NavLink></li>
  			<li><NavLink activeClassName="active" exact to="/About">About</NavLink></li>
  			<li><NavLink activeClassName="active" exact to="/Drivers">Drivers</NavLink></li>
  			<li><NavLink activeClassName="active" exact to="/AddDriver">Add Driver</NavLink></li>
  		</ul>
  	</nav>





        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/About">
            <About />
          </Route>
          <Route path="/Drivers">
            <DriversPage arts={arts} />
          </Route>
          <Route path="/AddDriver">
            <AddDriver addArt={addArt}/>
          </Route>
        </Switch>
      </BrowserRouter>
    </div>
  )
}

export default App;
