import { Link } from 'react-router-dom';
import { useState } from 'react';

function Load({ load, deleteLoad }) {
  const [onTheWay, setOnTheWay] = useState(true)

  function handleDelete() {
    fetch(`http://localhost:9292/Loads/${load.id}`, {
      method: "DELETE"
    })
    .then(r => r.json())
    .then(data => {
      deleteLoad(load.id)
    })
  }

