function DriverCard({ name, img_url, onUpdateDriver }) { 
  const [drugTestPassed, setDrugTestPassed] = useState(true)

  function handleDrugTest() {
    setDrugTestPassed(drugTestPassed => !drugTestPassed)
    fetch(`http://localhost:9292/${match.url}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      passed_drug_test: !drugTestPassed
    })
  })
    .then((r) => r.json())
    .then((updated) => {
      console.log(updated)
      onUpdateDriver(updated)
    });
  }

  return (
    <div className="card">
      <img src={img_url} alt={"driver picture"} />
      <h3 className="driver-name">{name}</h3>
      <hr></hr>
        <p> Check the box below if the driver passed on the drug test.</p>
        <button className="selected" onClick={handleDrugTest}>{drugTestPassed ? "☑️" : "☐"}</button>
    </div>
  )
}

export default DriverCard;
